console.log("Proyecto Animaciones CSS + JS cargado");const cajas=document.querySelectorAll(".reveal-caja");const observer=new IntersectionObserver((entries)=>{entries.forEach((entry)=>{if(entry.isIntersecting){entry.target.classList.add("visible")}})},{threshold:0.2,});cajas.forEach((caja)=>observer.observe(caja));const botonRebote=document.getElementById("boton-rebote");botonRebote.addEventListener("click",()=>{botonRebote.classList.add("rebotar");setTimeout(()=>{botonRebote.classList.remove("rebotar")},600)});const cuadroHover=document.getElementById("cuadro-hover-js");cuadroHover.addEventListener("mouseover",()=>{cuadroHover.classList.add("animacion-hover");setTimeout(()=>{cuadroHover.classList.remove("animacion-hover")},500)});const inputFocus=document.getElementById("input-focus");inputFocus.addEventListener("focus",()=>{inputFocus.classList.add("animacion-focus");setTimeout(()=>{inputFocus.classList.remove("animacion-focus")},600)});const cuadroZoom=document.getElementById("cuadro-zoom");cuadroZoom.addEventListener("click",()=>{cuadroZoom.classList.add("zoom-in");setTimeout(()=>{cuadroZoom.classList.remove("zoom-in")},500)});const cuadroRaf=document.getElementById("cuadro-raf");let posicion=0;let direccion=1;function animarCuadro(){const anchoContenedor=document.querySelector(".raf").clientWidth;if(posicion>=anchoContenedor-60){direccion=-1}else if(posicion<=0){direccion=1}
posicion+=direccion*2;cuadroRaf.style.left=`${posicion}px`;requestAnimationFrame(animarCuadro)}
requestAnimationFrame(animarCuadro)